.sign-in-form {
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: $form-bg;
  padding: 3rem 1rem;
  max-width: $lg-form-max-width;
  width: 100%;
  box-shadow: $shadow-md;
  border-radius: 0.375rem;
  margin: 1rem 0.25rem;

  @media (min-width: 640px) {
    padding-left: 1.5rem;
    padding-right: 1.5rem;
    min-width: $lg-form-max-width;
    width: 100vw;
  }

  @media (min-width: 1024px) {
    padding-left: 2rem;
    padding-right: 2rem;
    box-shadow: none;
    border-radius: 0;
    margin: 0;
  }

  @media (min-width: 1440px) {
    max-width: $xl-form-max-width;
    min-width: $xl-form-max-width;
  }

  a {
    font-weight: 500;
    color: $primary-color;

    &:hover {
      color: darken($color: $primary-color, $amount: 10);
    }
  }

  // Top part
  .logo {
    height: 4rem;
    width: auto;
    text-align: left;
    display: block;
  }

  .title {
    margin: 0;
    margin-top: 1.5rem;
    font-size: 1.875rem;
    line-height: 2.25rem;
    font-weight: 800;
    text-align: left;
  }

  .subtitle {
    margin-top: 0.5rem;
    text-align: left;
    font-size: 0.875rem;
    line-height: 1.25rem;
    color: $text-gray-600;
  }

  // Middle part
  .form-card {
    // padding: 2rem 1rem;
    border-radius: 0.375rem;
    margin-top: 2rem;

    @media (min-width: 640px) {
      // padding: 2rem 2.5rem;
    }
  }

  label {
    font-weight: 500;
    color: $text-gray-600;
  }

  [type="email"],
  [type="password"] {
    display: block;
    position: relative;
    font-size: 0.875rem;
    line-height: 1.25rem;
    width: 100%;
    flex: 1 1 0%;
    padding: 0.5rem 0.75rem;
    border-radius: 0.375rem;
    font-weight: 400;
    border: 1px solid $text-gray-300;
    background-clip: padding-box;
    transition: box-shadow 0.15s ease-in-out;
    box-shadow: $shadow-sm;

    appearance: none;
    -webkit-appearance: none;
    -moz-appearance: none;

    -webkit-box-sizing: border-box;
    /* Safari/Chrome, other WebKit */
    -moz-box-sizing: border-box;
    /* Firefox, other Gecko */
    box-sizing: border-box;
    /* Opera/IE 8+ */

    &:focus {
      outline: none;
      z-index: 10;
      box-shadow: 0 0 0 0.1rem rgba($color: rgba($primary-color, 1), $alpha: 1);
    }
  }

  [type="checkbox"] {
    // Simple resets
    appearance: none;
    -webkit-appearance: none;
    -moz-appearance: none;

    user-select: none;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;

    color-adjust: exact;
    -webkit-print-color-adjust: exact;

    display: inline-block;
    padding: 0;
    vertical-align: middle;
    background-origin: border-box;

    flex-shrink: 0;
    height: 1rem;
    width: 1rem;
    color: #2563eb;
    background-color: #fff;
    border: 1px solid #6b7280;

    // Checked state
    &:checked {
      border-color: transparent;
      background-color: currentColor;
      background-size: 100% 100%;
      background-position: center;
      background-repeat: no-repeat;

      &:hover,
      &:focus {
        border-color: transparent;
        background-color: currentColor;
      }
    }

    // Focused state
    &:focus {
      outline: 2px solid transparent;
      --tw-ring-inset: var(--tw-empty, /*!*/ /*!*/);
      --tw-ring-offset-width: 2px;
      --tw-ring-offset-color: #fff;
      --tw-ring-color: currentColor;
      --tw-ring-offset-shadow: var(--tw-ring-inset) 0 0 0
        var(--tw-ring-offset-width) var(--tw-ring-offset-color);
      --tw-ring-shadow: var(--tw-ring-inset) 0 0 0
        calc(2px + var(--tw-ring-offset-width)) var(--tw-ring-color);
      box-shadow: var(--tw-ring-offset-shadow), var(--tw-ring-shadow),
        var(--tw-shadow, 0 0 #0000);
    }
  }

  [type="checkbox"] {
    // Applying style
    border-radius: 0.25rem;
    border-color: $text-gray-300;
    color: $primary-color;

    // Checked state
    &:checked {
      background-image: url("../../../assets/images/white-check.svg");
    }
  }

  // Bottom part
  .btn {
    width: 100%;
    position: relative;

    &:focus {
      outline: none;
      box-shadow: 0 0 0 0.15rem
        rgba($color: rgba($primary-color, 1), $alpha: 0.5);
    }
  }

  .btn-theme {
    color: white;
    background-color: rgba($primary-color, 1);
    border: 1px solid rgba($primary-color, 1);

    &:hover {
      background-color: darken($color: rgba($primary-color, 1), $amount: 3%);
    }
  }

  .lock-icon {
    position: absolute;
    left: 0;
    top: 0;
    bottom: 0;
    padding-left: 0.75rem;
    display: flex;
    align-items: center;
    color: lighten($color: $primary-color, $amount: 20);
  }

  .separator {
    width: 100%;
    text-align: center;
    line-height: 0.1em;
    border-bottom: 1px solid $text-gray-200;
    margin-bottom: 2rem;
    margin-top: 2rem;
    color: $text-gray-500;

    span {
      background-color: white;
      padding-left: 0.5rem;
      padding-right: 0.5rem;
    }
  }

  .social-login {
    display: flex;
    user-select: none;
    gap: 1rem;
    flex-wrap: wrap;

    > * {
      flex-grow: 1;
    }

    svg,
    img {
      flex-shrink: 0;
    }
  }
}

.bg-image {
  display: none;

  @media (min-width: 1024px) {
    display: flex;

    img {
      height: 100%;
      width: 100%;
      object-fit: cover;
    }
  }
}
</style>
